import { useDispatch, useSelector } from "react-redux";
import { getPokemonId2 } from "../../Redux/Actions/action";
import { useEffect } from "react";
import { useParams } from "react-router-dom";
import style from "./Details.module.css";

const Detail = () => {
  const { id } = useParams();

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getPokemonId2(id));  
  }, []);

  const pokemonIds = useSelector((state) => state.pokemonId);

  const {
    name,
    image,
    life,
    attack,
    defense,
    velocity,
    height,
    weight,
    Types,
  } = pokemonIds;

  return (
    <div>
      {name ? (
        <div className={style.Detail_div}>
          <div>
            <img src={image} alt={name} />
          </div>
          <div>
              <h3>{name}</h3>
            </div>
          <div className={style.name_caracteristicas}> 
            <div className={style.Div_caracteristicas}>
              <div className={style.div_caract}>
                <span>Life: </span>
                <span>{life}</span>
              </div>
              <div className={style.div_caract}>
                <span>Ataque: </span>
                <span>{attack}</span>
              </div>
              <div className={style.div_caract}>
                <span>Defense: </span>
                <span>{defense}</span>
              </div >
              <div className={style.div_caract}>
                <span>Velocity: </span>
                <span>{velocity}</span>
              </div>
              <div className={style.div_caract}>
                <span>Height: </span>
                <span>{height} mts.</span>
              </div>
              <div className={style.div_caract}>
                <span>Weight: </span>
                <span>{weight} kgs.</span>
              </div>
              <div className={style.div_caract}>
                <span>Types: </span>
                <div>
                  {Types[0]}
                </div>
                <div>
                  {Types[1]}
                </div>
              </div>
            </div>
          </div>
        </div>
      ) : (
        "loading"
      )}
    </div>
  );
};
export default Detail;
