import { GET_ALL_POKEMONS, GET_POKEMON_ID, NEXT_PAGE, PREVIOUS_PAGE } from "../Actions/TypeActions"




const initialState = {
    allPokemon: [],
    currentPage: 0,
    pokemonsPerPage:12,
    
}


const rootReducer = (state = initialState, action) => {
    switch (action.type) {

        case GET_ALL_POKEMONS:
            return { ...state, allPokemon: action.payload};

        case GET_POKEMON_ID:
            return { ...state, allPokemon: action.payload};

        case NEXT_PAGE:
            return { ...state, currentPage: state.currentPage+1};
            
        case PREVIOUS_PAGE:
            return { ...state, currentPage: state.currentPage-1};



        default:
            return { ...state }
    }


}

export default rootReducer
