import { useDispatch, useSelector } from "react-redux";
import { getPokemonId2 } from "../../Redux/Actions/action";
import { useEffect } from "react";
import { useParams } from "react-router-dom";

const Detail = () => {
  const { id } = useParams();

  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getPokemonId2(id));
  }, []);

  const pokemonId = useSelector((state) => state.pokemonId);
  console.log(pokemonId);
  const {
    name,
    image,
    life,
    attack,
    defense,
    velocity,
    height,
    weight,
    Types,
  } = pokemonId;

  return (
    <div>
      <div>
        <img src={image} alt={name} />
      </div>

      <div>
        <h3>{name}</h3>
      </div>
      <div>
        <div>
          <span>Life: </span>
          <span>{life}</span>
        </div>
        <div>
          <span>Ataque: </span>
          <span>{attack}</span>
        </div>
        <div>
          <span>Defense: </span>
          <span>{defense}</span>
        </div>
        <div>
          <span>Velocity: </span>
          <span>{velocity}</span>
        </div>
        <div>
          <span>Height: </span>
          <span>{height}</span>
        </div>
        <div>
          <span>Weight: </span>
          <span>{weight}</span>
        </div>
        <div>
          <span>Types: </span>
          <div>
            <span>{Types[0]}</span>
          </div>
          <div>
            <span>{Types[1]}</span>
          </div>
        </div>
      </div>
    </div>
  );
};
export default Detail;
