import { useState } from "react";
import { useDispatch } from "react-redux";
import { searchName, getPokemonId } from "../../Redux/Actions/action";
import style from "./SearchBar.module.css"

const SearchBar = () => {
  // const onePoke = useSelector((poke) => poke.allPokemon);
  const dispatch = useDispatch();


  const [name, setName] = useState("")



  //MI HANDLE PARA SOBREESCRIBIR EL ESTADO LOCAL ID
  function handleChange(events) {
    setName(events.target.value)
}


// MI HANDLE PARA HACER DISPATCH DE MI ACTION FUNCION CON EL "ID"
  const getPokemonHandler = () => {

    dispatch(searchName(name))
  };


//MI HANDLE PARA RANDOMEAR POKEMONS
  const getRandomPokemon = ()=>{
    const random = Math.floor(Math.random()*1010)
    dispatch(getPokemonId(random))
  }



  return (
    <div className={style.SearchBar_Div}>
      <div>
        <button onClick={getRandomPokemon}>Random</button>

        <div className={styles['wave-group']}>
      <input required type="text" className={styles.input} value={name} onChange={handleChange}/>
      <span className={styles.bar}></span>
      <label className={styles.label}>
        <span className={styles['label-char']} style={{ '--index': 0 }}>P</span>
        <span className={styles['label-char']} style={{ '--index': 1 }}>o</span>
        <span className={styles['label-char']} style={{ '--index': 2 }}>k</span>
        <span className={styles['label-char']} style={{ '--index': 3 }}>e</span>
        <span className={styles['label-char']} style={{ '--index': 4 }}>m</span>
        <span className={styles['label-char']} style={{ '--index': 5 }}>o</span>
        <span className={styles['label-char']} style={{ '--index': 6 }}>n</span>
      </label>
    </div>

        <button onClick={getPokemonHandler}>Search</button>
      </div>
    </div>
  );
};

export default SearchBar;
